import android.app.Application
import android.util.Log
import com.runanywhere.sdk.public.RunAnywhere
import com.runanywhere.sdk.data.models.SDKEnvironment
import com.runanywhere.sdk.public.extensions.addModelFromURL
import com.runanywhere.sdk.llm.llamacpp.LlamaCppServiceProvider
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.GlobalScope
import kotlinx.coroutines.launch

/**
 * Application class for EduAI Tutor
 * Initializes RunAnywhere SDK for on-device AI
 */
class EduAIApplication : Application() {

 override fun onCreate() {
 super.onCreate()

 // Initialize RunAnywhere SDK asynchronously
 GlobalScope.launch(Dispatchers.IO) {
 initializeRunAnywhereSDK()
 }
 }

 private suspend fun initializeRunAnywhereSDK() {
 try {
 Log.i("EduAI", "Initializing RunAnywhere SDK...")

 // Step 1: Initialize SDK
 RunAnywhere.initialize(
 context = this@EduAIApplication,
 apiKey = "dev", // Any string works in dev mode
 environment = SDKEnvironment.DEVELOPMENT
 )

 // Step 2: Register LLM Service Provider
 LlamaCppServiceProvider.register()

 // Step 3: Register AI Models
 registerModels()

 // Step 4: Scan for previously downloaded models
 RunAnywhere.scanForDownloadedModels()

 Log.i("EduAI", "RunAnywhere SDK initialized successfully!")

 } catch (e: Exception) {
 Log.e("EduAI", "SDK initialization failed: ${e.message}", e)
 }
 }

 private suspend fun registerModels() {
 try {
 // Register Qwen 2.5 0.5B model - optimized for education (374 MB)
 addModelFromURL(
 url = "https://huggingface.co/Triangle104/Qwen2.5-0.5B-Instruct-Q6_K-GGUF/resolve/main/qwen2.5-0.5b-instruct-q6_k.gguf",
 name = "Qwen 2.5 0.5B Instruct Q6_K",
 type = "LLM"
 )
 Log.i("EduAI", "AI model registered successfully")
 } catch (e: Exception) {
 Log.e("EduAI", "Model registration failed: ${e.message}", e)
 }
 }
}
