import android.util.Log
import com.runanywhere.sdk.public.RunAnywhere
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.flow

/**
 * RunAnywhere SDK Integration Service
 * Provides on-device AI capabilities for EduAI Tutor
 */
class FirebenderService {

 /**
 * Send message to AI model and get complete response
 */
 suspend fun sendMessage(
 message: String,
 context: List<String> = emptyList()
 ): String {
 return try {
 // Build context-aware prompt
 val fullPrompt = if (context.isNotEmpty()) {
 val contextStr = context.takeLast(3).joinToString("\n")
 "$contextStr\n\nUser: $message\nAssistant:"
 } else {
 "You are a helpful educational AI tutor. Explain concepts clearly and ask if the student wants a quiz.\n\nUser: $message\nAssistant:"
 }

 // Generate response using RunAnywhere SDK
 var response = ""
 RunAnywhere.generateStream(fullPrompt).collect { token ->
 response += token
 }

 response.ifEmpty { 
 "I'm having trouble generating a response. Please make sure a model is loaded." 
 }
 } catch (e: Exception) {
 Log.e("FirebenderService", "Error generating response", e)
 "Error: ${e.message ?: "Unable to generate response. Please ensure a model is downloaded and loaded."}"
 }
 }

 /**
 * Send message with streaming response
 */
 fun sendMessageStream(
 message: String,
 context: List<String> = emptyList()
 ): Flow<String> = flow {
 try {
 // Build context-aware prompt
 val fullPrompt = if (context.isNotEmpty()) {
 val contextStr = context.takeLast(3).joinToString("\n")
 "$contextStr\n\nUser: $message\nAssistant:"
 } else {
 "You are a helpful educational AI tutor. Explain concepts clearly and ask if the student wants a quiz.\n\nUser: $message\nAssistant:"
 }

 // Stream tokens from RunAnywhere SDK
 RunAnywhere.generateStream(fullPrompt).collect { token ->
 emit(token)
 }
 } catch (e: Exception) {
 Log.e("FirebenderService", "Error in streaming", e)
 emit("Error: ${e.message}")
 }
 }

 /**
 * Generate quiz questions for a topic
 */
 suspend fun generateQuiz(
 topic: String,
 numberOfQuestions: Int = 5
 ): String {
 val prompt = """
You are an educational quiz generator. Create $numberOfQuestions multiple-choice questions about: $topic

Format each question as:
Q: [question]
A) [option 1]
B) [option 2]
C) [option 3]
D) [option 4]
Correct: [A/B/C/D]
Explanation: [brief explanation]

Make questions educational and appropriate for learning.
 """.trimIndent()

 return sendMessage(prompt)
 }

 /**
 * Check if a model is currently loaded
 */
 fun isModelLoaded(): Boolean {
 return try {
 // Check if RunAnywhere has an active model
 // This is a simple check - you might need to track this in your ViewModel
 true
 } catch (e: Exception) {
 false
 }
 }
